                        </div>

                        {bar}
                        <div className={styles.enterKilometerDiv} style={{ width: '95%', justifyContent: 'initial', marginTop: '20px' }}>
                            <HiOutlineLocationMarker color={GREEN} style={{ margin: '0px', marginRight: '10px' }} className={styles.driverHistory_ICON} />
