//driver sign in and sign up
import React from 'react';
import styles from './layout.module.css';
import Head from 'next/head';
import { Logo } from '../../../functions/images/vectors';
import { FaEnvelope } from 'react-icons/fa';
import { BsFillLockFill, BsEye, BsEyeSlash } from 'react-icons/bs';
import { CgKeyboard } from 'react-icons/cg';
import ReactCardFlip from 'react-card-flip';
import Truncate from 'react-truncate';
import Router from 'next/router';
import { signIn, signUp, signOut, sendPasswordResetLink } from '../../../functions/functions';
import Loader from 'react-loader-spinner'
const [WHITE] = ['#FFFFFF'];


export default class index extends React.Component {
    constructor() {
        super();

        this.state = {
            error: false,
            errorMessage: '',
            isFlipped: false,

            displayVerification: false,//false at first then if it is not verified then you have to display it,

            email: '',
            password: '',
            showPassword: false,
            forgotPassword: false,

            firstName: '',
            lastName: '',
            email1: '',
            phoneNumber: '',
            password1: '',
            confirmPassword: '',
            showPassword1: false,
            showPassword2: false,

            loading: false,
            width: '100vw', height: '100vh',
        };
    };
    componentDidMount() {
        window.addEventListener('resize', this.updateWindowDimensions);
    };
    componentWillUnmount() {
        window.removeEventListener('resize', this.updateWindowDimensions);
    }

    updateWindowDimensions = () => {
        this.setState({ width: window.innerWidth, height: window.innerHeight });
    }
    render() {
        return (
            <div className={styles.container}>
                <Head>
                    <title>Sign In or Sign Up | Perch</title>
                    <meta name="viewport" content="width=device-width, initial-scale=1"></meta>
                    <link rel="icon" type="image/png" sizes="32x32" href="/favicon-32x32.png" />
                    <link rel="icon" type="image/png" sizes="16x16" href="/favicon-16x16.png" />
                    <link rel="apple-touch-icon" sizes="180x180" href="/apple-touch-icon.png" />
                    <link rel="manifest" href="/site.webmanifest" />
                    <link rel="mask-icon" href="/safari-pinned-tab.svg" color="#5bbad5" />
                    <meta name="theme-color" content="#ffffff" />
                    <html lang="en"></html>
                </Head>

                {this.state.isFlipped ?
                    <img src="/d_su.svg" alt="Sign in" className={styles.img} style={{ height: this.state.height, width: this.state.width }} /> :
                    <img src="/d_si.svg" alt="Log in" className={styles.img} style={{ height: this.state.height, width: this.state.width }} />}
                {
                    this.state.displayVerification ?
                        <div className={styles.card} ///SIGN IN
                        >
                            <a className={styles.logo} href='/'><Logo color={'#4EB848'} /></a>
                            <p style={{ marginBottom: '10px' }} className={styles.title}>Contact Verification</p>
                            <p className={styles.cvTitle} >Enter the verification code sent to the number <span style={{ color: "#4EB848", display: 'inline-block' }}>{`+(919)110-7893`}</span></p>
                            <div className={styles.inputCont_} style={{ marginBottom: '5px' }}>
                                <div className={styles.inputContHalf} style={{ width: '65%', marginBottom: '0px' }}>
                                    <CgKeyboard color={'#4EB848'} size={20} style={{ marginRight: '10px', marginLeft: '10px' }} />
                                    <input type="text" placeholder="Code" className={styles.pH} />
                                </div>
                                <div className={styles.button1} style={{ width: '30%' }}>
                                    <p className={styles.buttonText1}>Verify</p>
                                </div>
                            </div>
                            <p className={styles.cvTitle} style={{ fontSize: 'smaller' }}>Didnt get the code yet? <span className={styles.cursor} style={{ color: "#005BEA", display: 'inline', fontFamily: 'Gilroy-Semibold' }}>Resend</span></p>

                            <p className={styles.cvTitle} style={{ marginTop: '10px' }}>Enter the verification code sent to the email <span style={{ color: "#4EB848", display: 'inline-block' }}>{`test2test.com`}</span></p>
                            <div className={styles.inputCont_} style={{ marginBottom: '5px', }}>
                                <div className={styles.inputContHalf} style={{ width: '65%', marginBottom: '0px' }}>
                                    <CgKeyboard color={'#4EB848'} size={20} style={{ marginRight: '10px', marginLeft: '10px' }} />
                                    <input type="text" placeholder="Code" className={styles.pH} />
                                </div>
                                <div className={styles.button1} style={{ width: '30%' }}>
                                    <p className={styles.buttonText1}>Verify</p>
                                </div>
                            </div>
                            <p className={styles.cvTitle} style={{ fontSize: 'smaller', }}>Didnt get the code yet? <span className={styles.cursor} style={{ color: "#005BEA", display: 'inline', fontFamily: 'Gilroy-Semibold' }}>Resend</span></p>


                            {this.state.error ?
                                <Truncate lines={1} ellipsis={'...'} className={styles.em}>
                                    {this.state.errorMessage}
                                </Truncate> :
                                <></>}
                            <a className={styles.button1} style={{ marginTop: this.state.error ? '0px' : '25px' }}
                                onClick={() => {
                                    // this.setState({ error: !this.state.error })
                                }}
                            >
                                <p className={styles.buttonText1}>Complete verification</p>
                            </a>
                            <a className={styles.fp_}><p className={styles.fp} style={{ fontSize: 'smaller' }}>Change phone number or email</p></a>
                            <div className={styles.inputCont_d} style={{ marginTop: '30px', }}>
                                <p className={styles.cu_} style={{ margin: '0px' }}>Running into an issue? <a href='/contact_us'><p className={styles.cu} style={{ color: '#4EB848' }}>Contact us</p></a></p>
                                <p
                                    className={styles.cu}
                                    style={{ fontFamily: 'Gilroy-Semibold', color: '#FF0000', margin: '0px' }}
                                    onClick={() => {
                                        this.setState({ email: '', password: '', displayVerification: false, isFlipped: false });
                                        signOut.call(this, true);
                                    }}>Sign out</p>
                            </div>
                        </div>
                        :
                        <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="horizontal" cardZIndex={2} containerStyle={{ marginRight: '5vw' }}>
                            <div className={styles.card}///SIGN IN
                                style={{ marginRight: '0px' }}
                            >
                                <a className={styles.logo} href='/'><Logo color={'#4EB848'} /></a>
                                {
                                    this.state.forgotPassword ?
                                        <>
                                            <p className={styles.title}>Forgot Password</p>

                                            <div className={styles.inputCont}>
                                                <FaEnvelope color={'#4EB848'} className={styles.env} />
                                                <input type="text"
                                                    placeholder="Email"
                                                    className={styles.pH}
                                                    value={this.state.email}
                                                    onChange={(event) => { this.setState({ email: event.target.value }) }}
                                                />
                                            </div>

                                            {this.state.error ?
                                                <Truncate lines={1} ellipsis={'...'} className={styles.em}>
                                                    {this.state.errorMessage}
                                                </Truncate> :
                                                <></>}
                                            <a
                                                className={styles.button1}
                                                style={{ marginTop: this.state.error ? '0px' : '25px' }}
                                                onClick={() => {
                                                    if (!this.state.loading) {
                                                        if (this.state.email == '')
                                                            this.setState({ error: true, errorMessage: 'Please enter your email' });
                                                        else
                                                            sendPasswordResetLink.call(this, this.state.email,);
                                                    }
                                                }} >
                                                {this.state.loading ?
                                                    <Loader
                                                        type="TailSpin"
                                                        color={WHITE}
                                                        height={'20px'}
                                                        width={'20px'} /> :
                                                    <p className={styles.buttonText1}>Log in</p>
                                                }
                                            </a>
                                            <a className={styles.fp_} onClick={() => { this.setState({ forgotPassword: false, errorMessage: '', error: false }) }}><p className={styles.fp}>Go back</p></a>
                                            <p className={styles.cu_}>Running into an issue? <a href='/contact_us'><p className={styles.cu}>Contact us</p></a></p>
                                            <p className={styles.ca}>Don't have an account</p>
                                            <a className={styles.caC}
                                                onClick={() => {
                                                    this.setState({ isFlipped: !this.state.isFlipped, error: false, errorMessage: '', }, () => {
                                                        this.setState({ forgotPassword: false, })
                                                    })
                                                }}>
                                                <p className={styles.ca_}>Create a new account</p>
                                            </a></> :
                                        <>
                                            <p className={styles.title}>Sign In</p>

                                            <div className={styles.inputCont}>
                                                <FaEnvelope color={'#4EB848'} className={styles.env} />
                                                <input type="text"
                                                    placeholder="Email"
                                                    className={styles.pH}
                                                    value={this.state.email}
                                                    onChange={(event) => { this.setState({ email: event.target.value }) }}
                                                />
                                            </div>
                                            <div className={styles.inputCont}>
