function timeAndDate(time, month, year) {
  var slash1 = 0,
      slash2 = 0,
      slash3 = 0;

  for (var k = 0; k < time.length; k++) {
    if (time.charAt(k) == '-') slash1 == 0 ? slash1 = k : slash2 == 0 ? slash2 = k : slash3 = k;
  }

  var HOUR = Number(time.substring(slash1 + 1, slash2)) == 0 ? 12 : Number(time.substring(slash1 + 1, slash2)) > 12 ? Number(time.substring(slash1 + 1, slash2)) - 12 : Number(time.substring(slash1 + 1, slash2));
  var MIN = Number(time.substring(slash2 + 1, slash3)) < 10 ? "0" + time.substring(slash2 + 1, slash3) : Number(time.substring(slash2 + 1, slash3));
  var MERIDIAN = Number(time.substring(slash1 + 1, slash2)) < 12 ? 'AM' : 'PM';
  var DAY = time.substring(0, slash1);
  var MONTH = M.indexOf(month) + 1;
  var formattedDate = "".concat(DAY, "/").concat(MONTH, "/").concat(year, ", ").concat(HOUR, ":").concat(MIN, " ").concat(MERIDIAN);
  return formattedDate;
}

;

