import firebase from 'firebase';
import React from 'react';
import "react-datepicker/dist/react-datepicker.css";
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import { colors, formatDateAllValues } from '../../functions';
import styles from '../panel_layout.module.css';

export default class PendingProcesses extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            result: {},
            loading: false,
            addProcess: true,//false,
            isNewProcess: false,
            userID: 'uid',
            type: 'rider',
            mainTopic: 'Missing Purse',
            timeCreated: 1628547712009,
            title: '',
            details: '',

            errorMessage: '',

        };
    };
    componentDidMount() {

    };
    renderFunctionToSubmit = () => {
        const { addProcess } = this.state;
        if (addProcess) {

        }
    };
    render() {
        let results = [];
        const keys = Object.keys(this.state.result).sort((a, b) => b - a);
        for (let k = 0; k < keys.length; k++) {
            const data = this.state.result[keys[k]];
            results.push(
                <>
                    <div
                        className={styles.previousMessageSum}
                        onClick={() => {
                            //this.setState({ currentData: data, index: k })
                        }}>
                        <div style={{ width: '90px', height: '30px', backgroundColor: data.type == 'rider' ? colors.GREEN : colors.BLUE, borderRadius: '5px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                            <p className={styles.readOrUnreadText} style={{ color: '#ffffff', fontFamily: 'Gilroy-ExtraBold' }}>{data.type == 'rider' ? 'Rider' : 'Driver'}</p>
                        </div>

                        <p style={{ fontSize: '105%', fontFamily: 'Gilroy-Bold', color: colors.GREEN }}>
                            <span style={{ color: colors.GREY }}>{displayDate}</span>
                        </p>
                    </div>
                    {
                        k == keys.length - 1 ?
                            <></> :
                            <div className={styles.sharedLine} style={{ marginTop: '5px' }}></div>
                    }
                </>
            )
        };
        const { addProcess, errorMessage, isNewProcess } = this.state;
        return (
            <div className={styles.cont}>
                <div className={styles.container} style={{ backgroundColor: colors.WHITE, marginTop: '20px' }}>
                    <p className={styles.title} style={{ color: colors.BLACK }}>List of pending requests</p>
                    {addProcess ?
                        <>
                            {isNewProcess ?
                                <>
                                    <div style={{ width: '90%', display: 'flex', flexDirection: 'row', justifyContent: 'space-between', marginBottom: '0px' }}>
                                        <input
                                            type="text"
                                            placeholder="User ID"
                                            className={styles.searchInput}
                                            style={{ width: '67%' }}
                                            value={this.state.userID}
                                            onChange={event => {
                                                this.setState({ userID: event.target.value });
                                            }}
                                        />
                                        <input
                                            type="text"
                                            placeholder="Type"
                                            className={styles.searchInput}
                                            style={{ width: '32%' }}
                                            value={this.state.type}
                                            onChange={event => {
                                                this.setState({ type: event.target.value.toLowerCase() });
                                            }}
                                        />
                                    </div>
                                    <input
                                        type="text"
                                        placeholder="Main topic"
                                        className={styles.searchInput}
                                        style={{ width: '90%', marginTop: '15px' }}
                                        value={this.state.mainTopic}
                                        onChange={event => {
                                            this.setState({ mainTopic: event.target.value });
                                        }}
                                    />
                                </> :
                                <>
                                    <div style={{ width: '90%', display: 'flex', flexDirection: 'row', justifyContent: 'space-between', marginBottom: '0px' }}>
                                        <p className={styles.mainText}>{`• User ID - ${this.state.userID}`}</p>
                                        <div style={{ width: '90px', height: '30px', backgroundColor: this.state.type == 'rider' ? colors.GREEN : colors.BLUE, borderRadius: '5px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                                            <p className={styles.readOrUnreadText} style={{ color: '#ffffff', fontFamily: 'Gilroy-ExtraBold' }}>{this.state.type == 'rider' ? 'Rider' : 'Driver'}</p>
                                        </div>
                                    </div>
                                    <div style={{ width: '90%', display: 'flex', flexDirection: 'row', justifyContent: 'space-between', marginBottom: '0px' }}>
                                        <p className={styles.mainText}>{`• Main Topic - ${this.state.mainTopic}`}</p>
                                        <p className={styles.mainText}>{`Created at - ${formatDateAllValues(this.state.timeCreated)}`}</p>
                                    </div>
                                </>}
                            <input
                                type="text"
                                placeholder="Title"
                                className={styles.searchInput}
                                style={{ width: '90%', marginTop: '15px' }}
                                value={this.state.title}
                                onChange={event => {
                                    this.setState({ title: event.target.value });
                                }}
                            />
                            <textarea
                                type="text"
                                placeholder="Title"
                                className={styles.contactUsContent}
                                style={{ width: '90%', marginTop: '15px', fontSize: '100%' }}
                                value={this.state.details}
                                onChange={event => {
                                    this.setState({ details: event.target.value });
                                }}
                            />
                        </>
                        :
                        <>

                        </>
                    }
                    {errorMessage != '' && <p
                        className={styles.em}
                        style={{ textAlign: 'initial' }}>{errorMessage}</p>}
                </div>

                <div className={styles.enterKilometerDiv}
                    style={{ marginTop: '20px', marginBottom: '150px', marginLeft: '2.5%', width: '95%' }}>
                    <div
                        className={styles.button2}
                        style={{ backgroundColor: colors.RED }}
                        id={styles.buttonBottom}
                        onClick={() => {

                        }}>
                        <p className={styles.buttonText1}>Cancel</p>
                    </div>
                    <div
                        className={styles.button2}
                        style={{ backgroundColor: colors.GREEN }}
                        id={styles.buttonBottom}
                        onClick={() => {

                        }}>
                        {this.state.loading ?
                            <Loader
                                type="TailSpin"
                                color={colors.WHITE}
                                height={'20px'}
                                width={'20px'} /> :
                            <p className={styles.buttonText1}>Add Pending Process</p>}
                    </div>
                </div>
            </div>
        )

    }
};
