                            style={{ backgroundColor: RED, maxWidth: 'initial', height: '40px', minHeight: 'initial', margin: '0px', width: '49%' }}
                            onClick={() => {
                                if (this.state.deleteAccountReason == '')
                                    this.setState({ errorMessage: 'Please enter a reason to delete your account' });
