export class ApplicationStatus extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            userID: this.props.userID,
            completed: [
                'done',
                'done',
                'ongoing',
                'undone',
                'undone',
            ],
            steps: [
                'Submit Application',
                'Upload Documents',
                'Verifying submitted documents',
                'Final Assessment / Interview',
                'Welcome to Perch!'
            ],
        };
    };
    componentDidMount() {
