                        </div>
                    </div>
                    {k == keys.length - 1 ? <></> : <div className={styles.sharedLine} style={{ marginTop: '5px' }}></div>}
                </>
            )
        }
        return (
            <div className={styles.cont}>
                <div className={styles.container} style={{ backgroundColor: WHITE, marginTop: '20px' }}>
                    <p className={styles.title} style={{ color: BLACK, marginBottom: '20px' }}>{this.state.showDocs ? <>{this.state.selected.carName} - <span style={{ color: GREEN }}>{this.state.selected.plateNumber}</span></> : 'Vehicle Applications'}</p>
                    {this.state.showDocs ?
                        <>
                            {requirements}
                            <img src={this.state.docVehicleImage} className={styles.driverVehicleIMAGE} />
                        </> :
                        results}
                </div>
                {this.state.showDocs ?
                    <div className={styles.enterKilometerDiv} style={{ marginTop: '20px', marginBottom: '150px', marginLeft: '2.5%', width: '95%' }}>
                        <div className={styles.button2} style={{ backgroundColor: RED }} id={styles.buttonBottom} onClick={() => { this.setState({ showDocs: false }) }}>
                            <p className={styles.buttonText1}>Cancel</p>
                        </div>

                        <div className={styles.button2} style={{ backgroundColor: GREEN }}
                            id={styles.buttonBottom}
                            onClick={() => {
                                if (this.state.inspectionVerified && this.state.insuranceVerified && this.state.registrationVerified) {
                                    const { userID, timestamp, dateFormat } = this.state.selected;
                                    validateVehicle.call(this, userID, timestamp, dateFormat);
                                    this.setState({ showDocs: false })
                                } else
                                    alert('Please verify all the documents by ticking the green button next to it');
                            }}>
                            <p className={styles.buttonText1}>Approve Vehicle</p>
                        </div>

                    </div> : <></>}

            </div>
        );
    };
};
export class DriverApplications extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            showDocs: false,
            result: {},
            docList: [
                "Driver's licence",
                "Proof of work eligibility",
                "EPS background check",
                "Vehicle insurance",
                "Vehicle registration",
                "Vehicle inspection",
            ],

            docDriversLicenseURL: false,
            docProofOfWorkEligibilityURL: false,
            docEPSBackgroundCheckURL: false,
            docRegistrationURL: '',
            docInsuranceURL: '',
            docInspectionURL: '',
            docVehicleImage: '',
            docProfilePicture: '',

            driversLicenseVerified: false,
            proofOfWorkEligibilityVerified: false,
            epsBackgroundCheckVerified: false,
            inspectionVerified: false,
            registrationVerified: false,
            insuranceVerified: false,

            selected: {},



        };
    }
    componentDidMount() {
        this.loadResult();
    }
    loadResult = () => {
        if (this.state.userDetails) {
            firebase.database().ref(`driverApplications`).once('value', snapshot => {
                this.setState({ result: snapshot.val() ? snapshot.val() : {} })
            });
        }
    };
    loadDocument = (userID) => {
        firebase.storage().ref(`driverApplication/${userID}/driversLicence`).getDownloadURL()
            .then(result => { this.setState({ docDriversLicenseURL: result }); })
            .catch(error => { console.log(error.message); });

        firebase.storage().ref(`driverApplication/${userID}/proofOfWorkEligibility`).getDownloadURL()
            .then(result => { this.setState({ docProofOfWorkEligibilityURL: result }); })
            .catch(error => { console.log(error.message) });

        firebase.storage().ref(`driverApplication/${userID}/epsBackgroundCheck`).getDownloadURL()
            .then(result => { this.setState({ docEPSBackgroundCheckURL: result }); })
            .catch(error => { console.log(error.message) });

        firebase.storage().ref(`driverApplication/${userID}/profilePicture`).getDownloadURL()
            .then(result => { this.setState({ docProfilePicture: result }); })
            .catch(error => { console.log(error.message) });

        firebase.storage().ref(`driverApplication/${userID}/vehicleRegistration`).getDownloadURL()
            .then(result => { this.setState({ docRegistrationURL: result }); })
            .catch(error => { console.log(error.message); });

        firebase.storage().ref(`driverApplication/${userID}/vehicleInsurance`).getDownloadURL()
            .then(result => { this.setState({ docInsuranceURL: result }); })
            .catch(error => { console.log(error.message) });

        firebase.storage().ref(`driverApplication/${userID}/vehicleInspection`).getDownloadURL()
            .then(result => { this.setState({ docInspectionURL: result }); })
            .catch(error => { console.log(error.message) });

        firebase.storage().ref(`driverApplication/${userID}/${documentStorageID}/vehicleImage`).getDownloadURL()
            .then(result => { this.setState({ docVehicleImage: result }); })
            .catch(error => { console.log(error.message) });
    };

    render() {
        let requirements = [];
        for (let i = 0; i < this.state.docList.length; i++) {

            let urlToShow = '';
            if (this.state.docList[i] == "Driver's licence")
                urlToShow = this.state.docDriversLicenseURL;
            else if (this.state.docList[i] == 'Proof of work eligibility')
                urlToShow = this.state.docProofOfWorkEligibilityURL;
            else if (this.state.docList[i] == 'EPS background check')
                urlToShow = this.state.docEPSBackgroundCheckURL;
            else if (this.state.docList[i] == 'Vehicle insurance')
                urlToShow = this.state.docInsuranceURL;
            else if (this.state.docList[i] == 'Vehicle registration')
                urlToShow = this.state.docRegistrationURL;
            else if (this.state.docList[i] == 'Vehicle inspection')
                urlToShow = this.state.docInspectionURL;

            let isVerified = false;
            if (this.state.docList[i] == "Driver's licence")
                isVerified = this.state.driversLicenseVerified;
            else if (this.state.docList[i] == 'Proof of work eligibility')
                isVerified = this.state.proofOfWorkEligibilityVerified;
            else if (this.state.docList[i] == 'EPS background check')
                isVerified = this.state.epsBackgroundCheckVerified;
            else if (this.state.docList[i] == 'Vehicle insurance')
                isVerified = this.state.insuranceVerified;
            else if (this.state.docList[i] == 'Vehicle registration')
                isVerified = this.state.registrationVerified;
            else if (this.state.docList[i] == 'Vehicle inspection')
                isVerified = this.state.inspectionVerified;



            requirements.push(
                <div className={styles.driverFAQ_PICDESC}
                    id={styles.driverROUNDSUPPORT}
                >
                    <a href={urlToShow}
                        target={'_blank'}>
                        <p className={styles.text} style={{ color: '#000000', width: 'initial', margin: '0px', marginLeft: '10px' }}>{this.state.docList[i]}</p>
                    </a>
                    <div className={styles.driver_APP_RADIO} style={{ backgroundColor: GREEN, opacity: isVerified ? 1 : 0.5 }}
                        onClick={() => {
                            if (this.state.docList[i] == "Driver's licence")
                                this.setState({ driversLicenseVerified: true });
                            else if (this.state.docList[i] == 'Proof of work eligibility')
                                this.setState({ proofOfWorkEligibilityVerified: true });
                            else if (this.state.docList[i] == 'EPS background check')
                                this.setState({ epsBackgroundCheckVerified: true });
                            else if (this.state.docList[i] == 'Vehicle insurance')
                                this.setState({ insuranceVerified: true });
                            else if (this.state.docList[i] == 'Vehicle registration')
                                this.setState({ registrationVerified: true });
                            else if (this.state.docList[i] == 'Vehicle inspection')
                                this.setState({ inspectionVerified: true });
                        }}
                    >
                        {isVerified ?
                            <AiOutlineCheck color={WHITE} size={'15px'} /> :
                            <></>
                        }
                    </div>
                </div>
            );
        }

        if (!this.state.userDetails && this.props.userDetails)
            this.setState({ userDetails: this.props.userDetails }, () => { this.loadResult(); });

        let results = [];
        const keys = Object.keys(this.state.result).sort((a, b) => {
            let stageA = 0, stageB = 0;
            switch (this.state.result[a].stage) {
                case 'one': { stageA = 1; } break;
                case 'two': { stageA = 2; } break;
                case 'three': { stageA = 3; } break;
            };
            switch (this.state.result[b].stage) {
                case 'one': { stageB = 1; } break;
                case 'two': { stageB = 2; } break;
                case 'three': { stageB = 3; } break;
            };

            //return (this.state.result[b].timestamp - this.state.result[a].timestamp);
            return (stageB - stageA);
        });
        for (let k = 0; k < keys.length; k++) {
            const userID = keys[k];
            const { timestamp, name, stage } = this.state.result[keys[k]];
            let stageToDisplay = 0;
            let color = YELLOW;
            switch (stage) {
                case 'one': {
                    stageToDisplay = `S1`;
                } break;
                case 'two': {
                    stageToDisplay = `S2`;
                } break;
                case 'three': {
                    stageToDisplay = `S3`;
                    color = GREEN;
                } break;
            };

            results.push(
                <>
                    <div
                        className={styles.previousMessageSum}
                        onClick={() => {
                            if (stage == 'three')
                                this.setState({
                                    showDocs: true,
                                    selected: {
                                        name: name,
                                        timestamp: timestamp,
                                        userID: userID,
                                    }
                                })
                        }}>
                        <p style={{ fontSize: '95%' }}>{name}</p>

                        <div style={{ display: 'flex', flexDirection: 'row', width: '150px', justifyContent: 'space-between' }}>
                            <span style={{ flexDirection: 'row', display: 'flex', alignItems: 'center', }}>
                                <div className={styles.previousMessageDate}>
                                    <p style={{ fontFamily: 'Gilroy-Semibold', fontSize: '75%', marginRight: '-10px', paddingLeft: '10px' }}>
                                        {dateOfQuery(timestamp)}
                                    </p>
                                </div>
                                <div style={{ width: '50px', height: '30px', backgroundColor: color, borderRadius: '5px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                                    <p className={styles.readOrUnreadText} style={{ color: '#ffffff', fontFamily: 'Gilroy-ExtraBold' }}>{stageToDisplay}</p>
                                </div>
                            </span>
                        </div>
                    </div>
                    {k == keys.length - 1 ?
                        <></> :
                        <div className={styles.sharedLine} style={{ marginTop: '5px' }}></div>
                    }
                </>
            )
        }


        return (
            <div className={styles.cont}>
                <div className={styles.container} style={{ backgroundColor: WHITE, marginTop: '20px' }}>
                    <p className={styles.title} style={{ color: BLACK, marginBottom: '20px' }}>{this.state.showDocs ? this.state.selected.name : 'Driver Applications'}</p>
                    {this.state.showDocs ?
                        <>
                            {requirements}
                        </> :
                        results}
                </div>
                {this.state.showDocs ?
                    <div className={styles.enterKilometerDiv} style={{ marginTop: '20px', marginBottom: '150px', marginLeft: '2.5%', width: '95%' }}>
                        <div
                            className={styles.button2}
                            style={{ backgroundColor: RED }}
                            id={styles.buttonBottom}
                            onClick={() => {
                                this.setState({ showDocs: false });
                            }}>
                            <p className={styles.buttonText1}>Cancel</p>
                        </div>

                        <div
                            className={styles.button2}
                            style={{ backgroundColor: GREEN }}
                            id={styles.buttonBottom}
                            onClick={() => {

                            }}>
                            <p className={styles.buttonText1}>Move to next stage</p>
                        </div>

                    </div> : <></>}

            </div>
        );
    };
};
