import Head from 'next/head';
import styles from './s/articles/layout.module.css';
import React from 'react';
import Header from './components/header/header';
import Footer from './components/footer/footer';
import Loader from 'react-loader-spinner';
import firebase from 'firebase';
import { sendFeedback } from '../functions/functions';
import ShareIcons from '../functions/shareIcons';
import Router from 'next/router';

export default class index extends React.Component {
    static async getInitialProps({ query }) {
        const { mode, oobCode } = query;

        return {
            mode: mode,
            oobCode: oobCode,
        }
    }

    constructor() {
        super();

        this.state = {
            password: '',
            confirmPassword: '',
            loading: false,
            errorMessage: ''
        };
    };
    componentDidMount() {

    };
    render() {

        let { mode, oobCode } = this.props;
        let title = '',
            subtitle = '',
            pageTitle = '',
            content = <></>;

        switch (mode) {
            case 'resetPassword': {
                pageTitle = 'Reset Password';
                title = 'Reset your password form';
                subtitle = 'Enter your new password and confirm it below.'
            } break;
            case 'recoverEmail': {
                pageTitle = 'Recover Email';
            } break;
            case 'verifyEmail': {
                pageTitle = 'Verify Email';
            } break;
        }

        return (
            <div className={styles.container}>
                <Head>
                    <title>{`${pageTitle} | Perch`}</title>
                    <meta name="viewport" content="width=device-width, initial-scale=1"></meta>
                    <link rel="icon" type="image/png" sizes="32x32" href="/favicon-32x32.png" />
                    <link rel="icon" type="image/png" sizes="16x16" href="/favicon-16x16.png" />
                    <link rel="apple-touch-icon" sizes="180x180" href="/apple-touch-icon.png" />
                    <link rel="manifest" href="/site.webmanifest" />
                    <link rel="mask-icon" href="/safari-pinned-tab.svg" color="#5bbad5" />
                    <meta name="theme-color" content="#ffffff" />
                    <html lang="en"></html>
                </Head>
                <Header />
                <img src="/articlesPageBack1.svg" alt="Perch Carpool" className={styles.mainPageBack1} />
                <div className={styles.body}>

                    <div className={styles.c_} style={{ marginTop: '15px', marginBottom: '20px', justifyContent: 'space-between', alignItems: 'center', }}>
                        <p className={styles.t1} style={{ margin: '0px' }}>{title}</p>
                        <ShareIcons />
                    </div>

                    {/* <p className={styles.subTitle} >{subtitle}</p> */}
                    <div className={styles.contactUsCont} style={{ paddingBottom: '30px' }}>
                        <p className={styles.contactUsTitle} style={{ marginTop: '20px', marginBottom: '0px' }}>{subtitle}</p>

                        <input type="text"
                            placeholder="Enter new password"
                            className={styles.contactUsEmail}
                            value={this.state.password}
                            onChange={(event) => { this.setState({ password: event.target.value }) }}
                        />

                        <input type="text"
                            placeholder="Re-enter your new password "
                            className={styles.contactUsEmail}
                            value={this.state.confirmPassword}
                            onChange={(event) => { this.setState({ confirmPassword: event.target.value }) }}
                        />


                        <p className={styles.em}>{this.state.errorMessage}</p>
                    </div>
                    <div className={styles.contactUsLC}>
                        <a className={styles.button2}
                            onClick={() => {
                                if (this.state.password == '')
                                    this.setState({ errorMessage: 'Please enter a new password' });
                                else if (this.state.confirmPassword != this.state.password)
                                    this.setState({ errorMessage: 'Passwords do not match' });
                                else
                                    sendFeedback.call(this, true);
                            }}>
                            {this.state.loading ?
                                <Loader
                                    type="TailSpin"
                                    color={'#ffffff'}
                                    height={'20px'}
                                    width={'20px'} /> :
                                <p className={styles.buttonText2}>Send Message</p>}
                        </a>
                        <p className={styles.contactUsLasttext}>
                            We respond to your inquires via your email. We would not send you promotional emails unless you are already a signed up Perch user.
                        </p>
                    </div>


                </div>
                <Footer />
            </div>
        )

    }
}
