export class VehicleApplications extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            showDocs: false,
            result: {},
            docList: [
                "Vehicle insurance",
                "Vehicle registration",
                "Vehicle inspection",
            ],
            selected: {},
            docRegisterationURL: '',
            docInsuranceURL: '',
            docInspectionURL: '',
            docVehicleImage: '',
            urlToShow: null,
        };
    }
    componentDidMount() {
        this.loadResult();
    }
    loadResult = () => {
        if (this.state.userDetails) {
            firebase.database().ref(`vehicleAdditionRequest`).once('value', snapshot => {
                this.setState({ result: snapshot.val() ? snapshot.val() : {} })
            });
        }
    };
    loadDocument = (userID) => {
        firebase.storage().ref(`driverAddVehicleApplication/${userID}/registeration`).getDownloadURL()
            .then(result => {
                this.state({ docRegisterationURL: result });
            })
            .catch(error => { console.log(error.message) });
        firebase.storage().ref(`driverAddVehicleApplication/${userID}/insurance`).getDownloadURL()
            .then(result => {
                this.state({ docInsuranceURL: result });
            })
            .catch(error => { console.log(error.message) });
        firebase.storage().ref(`driverAddVehicleApplication/${userID}/inspection`).getDownloadURL()
            .then(result => {
                this.state({ docInspectionURL: result });
            }).catch(error => { console.log(error.message) });
        firebase.storage().ref(`driverAddVehicleApplication/${userID}/vehicleImage`).getDownloadURL()
            .then(result => {
                this.state({ docVehicleImage: result });
            }).catch(error => { console.log(error.message) });
    };
    dateOfQuery(timestamp) {
        return `${formatAMPM(new Date(timestamp)).toUpperCase()} ${('0' + new Date(timestamp).getDate()).slice(-2)}/${('0' + (new Date(timestamp).getMonth() + 1)).slice(-2)}/${new Date(timestamp).getFullYear()}`;
    };
    render() {
        let requirements = [];
        for (let i = 0; i < this.state.docList.length; i++) {
            let urlToShow = '';
            if (this.state.docList[i] == 'Vehicle insurance')
                urlToShow = 'https://www.google.com/search?q=open+an+external+page+in+nextjs&sxsrf=ALeKk02g7akfzPhCrNvG-KuJq2OLsqKAyw%3A1623294014575&ei=PoDBYNbCIsTK-gSrg7GgCw&oq=open+an+external+page+in+nextjs&gs_&sclient=gws-wiz&ved=0ahUKEwjW2eq4iYzxAhVEpZ4KHatBDLQQ4dUDCA8&uact=5';
            else if (this.state.docList[i] == 'Vehicle registration')
                urlToShow = this.state.docRegisterationURL;
            else if (this.state.docList[i] == 'Vehicle inspection')
                urlToShow = this.state.docInspectionURL;


            requirements.push(

                <a className={styles.driverFAQ_PICDESC}
                href={urlToShow}
                target={'_blank'}
                    id={styles.driver_APP_TOP_RADIO_CONT_}
                    onClick={() => {
                        //this.setState({ urlToShow: true });
                    }}>
                    <p className={styles.text} style={{ color: GREY, width: 'initial', margin: '0px', marginLeft: '10px' }}>{this.state.docList[i]}</p>
                    <div className={styles.driver_APP_RADIO} style={{ backgroundColor: GREEN }}>
                        <AiOutlineCheck color={WHITE} size={'15px'} />
                    </div>
                </a>
            );
        }

        if (!this.state.userDetails && this.props.userDetails)
            this.setState({ userDetails: this.props.userDetails }, () => { this.loadResult(); });

        let results = [];
        const keys = Object.keys(this.state.result).sort();
        for (let k = 0; k < keys.length; k++) {
            const { year, make, model, timestamp } = this.state.result[keys[k]];
            const carName = `${year} ${make} ${model}`;
            results.push(
                <>
                    <div className={styles.previousMessageSum}
                        onClick={() => {
                            this.loadDocument(this.state.result[keys[k]].userID)
                            this.setState({
                                showDocs: true,
                                selected: {
                                    carName: carName,
                                    ...this.state.result[keys[k]],
                                },
                            })
                        }}>
                        <p style={{ fontSize: '95%' }}>{carName}</p>
                        <div className={styles.previousMessageDate}>
                            <p style={{ fontFamily: 'Gilroy-Semibold', fontSize: '75%', marginRight: '-10px', paddingLeft: '10px' }}>
                                {this.dateOfQuery(timestamp)}
                            </p>
                        </div>
                    </div>
                    {k == keys.length - 1 ? <></> : <div className={styles.sharedLine} style={{ marginTop: '5px' }}></div>}
                </>
            )
        }
        return (
            <div className={styles.cont}>
                <div className={styles.container} style={{ backgroundColor: WHITE, marginTop: '20px' }}>
                    <p className={styles.title} style={{ color: BLACK, marginBottom: '20px' }}>{this.state.showDocs ? this.state.selected.carName : 'Vehicle Applications'}</p>
                    {this.state.showDocs ?
                        requirements :
                        results}
                </div>
                {this.state.showDocs ?
                    <div className={styles.enterKilometerDiv} style={{ marginTop: '20px', marginBottom: '150px', marginLeft: '2.5%', width: '95%' }}>
                        <div className={styles.button2} style={{ backgroundColor: RED }} id={styles.buttonBottom} onClick={() => { this.setState({ showDocs: false }) }}>
                            <p className={styles.buttonText1}>Cancel</p>
                        </div>

                        <div className={styles.button2} style={{ backgroundColor: GREEN }} id={styles.buttonBottom} onClick={() => { }}>
                            <p className={styles.buttonText1}>Approve Vehicle</p>
                        </div>

                    </div> : <></>}

            </div>
        );
    };
};
