//admin dashboard
import React, { Component } from 'react';
import styles from './layout.module.css';
import Head from 'next/head';
import { ImExit } from "react-icons/im";
import { Logo, DashboardIcons } from '../../functions/images/vectors';
import Router from 'next/router';
import { AdminDashBoard, PerchWallet, ContactUs, TripHistory, Settings } from '../../functions/panels/panels';
import firebase from 'firebase';
import LoadingScreen from '../components/loadingScreen/loadingScreen';
export default class index extends React.Component {
    constructor() {
        super();

        this.state = {
            optionCode: 'db',
            width: 0, height: 0,
            loggedIn: 'NULL',
            userDetails: null,
            url: null,
        }
    };

    componentDidMount() {
        this.updateWindowDimensions();
        window.addEventListener('resize', this.updateWindowDimensions);
        firebase.auth().onAuthStateChanged(user => {
            if (user) {
                firebase.database().ref(`users/${user.uid}`).once('value', (snapshot) => {
                    this.setState({ userDetails: snapshot.val() });
                    this.setImage(snapshot.val().photoRef);
                }).catch(error => { console.log(error.message) });


                firebase.database().ref(`adminAuthorized/${user.uid}`).once(`value`, snapshot => {
                    if (snapshot.val())
                        this.setState({ loggedIn: 'TRUE' });
                    else
                        this.setState({ loggedIn: 'FALSE' });
                }).catch(error => {
                    this.setState({ loggedIn: 'FALSE' });
                })
            }
            else
                this.setState({ loggedIn: 'FALSE' });
        });
    }
    setImage = (photoRef) => {
        firebase.storage().ref(`${photoRef}`).getDownloadURL()
            .then(result => {
                this.setState({ url: result })
            }).catch(error => { console.log(error.message) });
    };
    componentWillUnmount() {
        window.removeEventListener('resize', this.updateWindowDimensions);
    }

    updateWindowDimensions = () => {
        this.setState({ width: window.innerWidth, height: window.innerHeight });
    }
    navigate = (x) => {
        switch (x) {
            case 'db': {
                this.setState({ optionCode: 'db' })
            } break;
            case 'th': {
                this.setState({ optionCode: 'th' })
            } break;
            case 'pw': {
                this.setState({ optionCode: 'pw' })
            } break;
            case 'cu': {
                this.setState({ optionCode: 'cu' })
            } break;
            case 'ss': {
                this.setState({ optionCode: 'ss' })
            } break;
        }
    }
    render() {
        if (this.state.loggedIn == 'FALSE')
            Router.push('/s/auth/u_si_su');
        if (this.state.loggedIn != 'TRUE')
            return <LoadingScreen />;



        let option = '';
        let content = <></>;
        switch (this.state.optionCode) {
            case 'db': {
                option = 'Dashboard';
                content = <AdminDashBoard />;
            } break;
            case 'th': {
                option = 'Trip History';
                content = <TripHistory />;
            } break;
            case 'pw': {
                option = 'Perch Wallet';
                content = <PerchWallet />;
            } break;
            case 'cu': {
                option = 'Contact Us';
                content = <ContactUs />;
            } break;
            case 'ss': {
                option = 'Settings';
                content = <Settings />
            } break;
        }
        return (
            <div className={styles.container} style={{ height: this.state.height != 0 ? this.state.height : '100vh' }}>

                <Head>
                    <html lang="en"></html>
                    <title>Admin Dashboard | Perch</title>
                    <meta name="viewport" content="width=device-width, initial-scale=1"></meta>
                    <link rel="icon" href="/favicon.ico" />
                </Head>

                <div className={styles.header}>
                    <div className={styles.logoBox}>
                        <div>
                            <div className={styles.logo}>
                                <Logo color={'#FFFFFF'} />
                            </div>
                        </div>
                    </div>
                    <div className={styles.optionShadowBox}>
                        <p className={styles.optionTitle}>{option}</p>
                        <div className={styles.textRow1}>
                            {
                                this.state.userDetails ?
                                    <>
                                        {this.state.url ?
                                            <img src={this.state.url} className={styles.dp} style={{ objectFit: 'cover', borderWidth: '0px' }} /> :
                                            <div className={styles.dp}></div>}
                                        <p className={styles.text_}>{`Hello, ${this.state.userDetails.firstName}`}</p>
                                    </> :
                                    <></>
                            }
                            <a className={styles.link_}><p className={styles.text}>How it works</p></a>
                            <a>
                                <div className={styles.circle}>
                                    <ImExit color={'#FFFFFF'} className={styles.logOut} />
                                </div>
                            </a>
                        </div>
                    </div>
                </div>

                <div className={styles.body}>
                    <div className={styles.couple}>
