        this.setState({ timer: 60 }, () => {
            const time = setInterval(() => {
                if (this.state.timer == 0)
                    clearInterval(time);
                else
                    this.setState({ timer: this.state.timer - 1 })
            }, 1000);
        });
        if (this.state.timer == 0)
            switch (type) {
                case 'email': {
                    sendVerification(this.state.userDetails.userID,
                        'email',
                        'storeAndSend',
                        '',
                        '',
                        this.state.newEmail,
                        this.state.userDetails.firstName);
