                                        //style={{ width: '150px', margin: '0px', fontSize: '90%', border: '1px solid rgba(149, 154, 172, 0.5)' }}
                                        value={this.state.vehicleYear}
                                        onChange={event => {
                                            this.setState({ vehicleYear: event.target.value });
