import React from "react";
import "react-datepicker/dist/react-datepicker.css";
import { AiOutlineClose, AiOutlineRight } from "react-icons/ai";
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import { colors } from "../../functions";
import styles from "../panel_layout.module.css";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import firebase from "firebase";

const stripePromise = loadStripe("pk_test_RjADdW2vGwFAgOOk7ws1juNB002JV727O8");

export default class PerchWallet extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      buyKilometers: false,
      addCard: false,
      userDetails: this.props.userDetails,
      quantity: 0,
      cards: [],
      selected: null,
    };
  }
  componentDidMount() {
    const { userID } = this.state.userDetails;
    firebase
      .database()
      .ref(`perchKilometers/${userID}/quantity`)
      .on("value", (snapshot) => {
        if (!!snapshot) this.setState({ quantity: snapshot.val() });
      });
    this.loadCards();
  }
  loadCards = () => {
    const { userID } = this.state.userDetails;
    firebase
      .database()
      .ref(`cards/${userID}`)
      .once("value", (snapshot) => {
        if (!!snapshot) {
          let arrayOfLast4 = [],
            i = 0;
          for (let key in snapshot.val()) {
            if (key != "selected")
              arrayOfLast4[i] = snapshot.val()[key].card.last4;
            else this.setState({ selected: key });
            i++;
          }
          this.setState({ cards: arrayOfLast4 });
        }
      });
  };
  render() {
    let cards = [];
    for (let i = 0; i < this.state.cards.length; i++)
      cards.push(
        <PaymentCard
          key={this.state.cards[i]}
          last4={this.state.cards[i]}
          selected={this.state.selected == this.state.cards[i]}
          type={this.state.buyKilometers ? "pickPayment" : "listOfCards"}
        />
      );
    return (
      <div className={styles.cont}>
        {this.state.buyKilometers ? ( //buy kilometers
          <>
            <div
              className={styles.container}
              style={{ backgroundColor: colors.WHITE, marginTop: "20px" }}
            >
              <p className={styles.title} style={{ color: colors.BLACK }}>
                Enter the amount of kilometers you would like to purchase
              </p>
              <input
                type="text"
                placeholder="Enter amount of kilometers"
                className={styles.inputPaymentPanel}
              />
              <div
                className={styles.enterKilometerDiv}
                style={{ marginTop: "20px" }}
              >
                <p
